var v = document.getElementById(`video_${micropot.id}`);
var b = document.getElementById(`playbtn_${micropot.id}`);
v.addEventListener(
    'play', 
    function() { 
        v.play();
    }, 
    false);

b.onclick = function() {
    if (v.paused) {
        v.play();
        document.getElementById(`play_${micropot.id}`).classList.add("none");
    } else {
        v.pause();
        document.getElementById(`play_${micropot.id}`).classList.remove("none");
    }
    return false;
};
