var v = document.getElementById('video');
var b = document.getElementById('playbtn');
v.addEventListener(
    'play', 
    function() { 
        v.play();
    }, 
    false);

b.onclick = function() {
    if (v.paused) {
        v.play();
        document.getElementById("play").classList.add("none");
        setTimeOut('document.getElementById("play").classList.remove("none")',1000);
        setTimeOut('document.getElementById("play").classList.add("nonenone")',1000);
    } else {
        v.pause();
        document.getElementById("play").classList.remove("nonenone");
        document.getElementById("play").classList.remove("none");
    }
    return false;
};
