ididid=$('.idid').val();
var v = document.getElementById(`video_311`);
var b = document.getElementById(`playbtn_311`);


b.onclick = function() {
    if (v.paused) {
        v.play();
        document.getElementById(`play_311`).classList.add("none");
    } else {
        v.pause();
        document.getElementById(`play_311`).classList.remove("none");
    }
};

