var trigger = document.getElementsByClassName("video");
var triggers = Array.from(trigger);
triggers.forEach(function(target) {
  var v = document.getElementById(`video`);
  target.onclick = function() {
    if (target.paused) {
        target.play();
        document.getElementById(`play`).classList.add("none");
    } else {
        target.pause();
        document.getElementById(`play`).classList.remove("none");
    }
  };
});
