var v = document.getElementById('video');
v.addEventListener(
    'play', 
    function() { 
        v.play();
    }, 
    false);

v.onclick = function() {
    if (v.paused) {
        v.play();
        document.getElementByclass("fa-play").classList.add("redred")
    } else {
        v.pause();
    }

    return false;
};
