ididid=$('.idid').val();
var v = document.getElementById(`video_${ididid}`);
var b = document.getElementById(`playbtn_${ididid}`);


b.onclick = function() {
    if (v.paused) {
        v.play();
        document.getElementById(`play_${ididid}`).classList.add("none");
    } else {
        v.pause();
        document.getElementById(`play_${ididid}`).classList.remove("none");
    }
    return false;
};
