var v = document.getElementById(`video_${micropost.id}`);
var b = document.getElementById(`playbtn_${micropost.id}`);
v.addEventListener(
    `play_${micropost.id}`, 
    function() { 
        v.play();
    }, 
    false);

b.onclick = function() {
    if (v.paused) {
        v.play();
        document.getElementById(`play_${micropost.id}`).classList.add("none");
    } else {
        v.pause();
        document.getElementById(`play_${micropost.id}`).classList.remove("none");
    }
    return false;
};
