var v = document.getElementById('video');
var b = document.getElementById('playbtn');
v.addEventListener(
    'play', 
    function() { 
        v.play();
    }, 
);

b.onclick = function() {
    if (v.paused) {
        v.play();
        document.getElementById("play").classList.add("none");
    } else {
        v.pause();
        document.getElementById("play").classList.remove("none");
    }
;
};
