var playbtn = document.querySelectorAll('.playbtn');
var video = document.querySelectorAll('.video');
var play = document.querySelectorAll('.fas fa-play');


  playbtn[0].onclick = function() {
    if (video[0].paused) {
        video[0].play();
        play[0].classList.add("none");
    } else {
      video[0].pause();
        play[0].classList.remove("none");
    }
  };
