var trigger = document.getElementsByClassName("playbtn");
var triggers = Array.from(trigger);
triggers.forEach(function(target) {
  var v = document.getElementsByClassName(`video`);
  target.onclick = function() {
    if (v.paused) {
        v.play();
        document.getElementsByClassName(`fa-play`).classList.add("none");
    } else {
        v.pause();
        document.getElementsByClassName(`fa-play`).classList.remove("none");
    }
  };
});
