var v = document.getElementById("video_<%= micropost.id%>");
var b = document.getElementById("playbtn_<%= micropost.id%>");
v.addEventListener(
    'play', 
    function() { 
        v.play();
    }, 
    false);

b.onclick = function() {
    if (v.paused) {
        v.play();
        document.getElementById("play").classList.add("none");
    } else {
        v.pause();
        document.getElementById("play").classList.remove("none");
    }
    return false;
};
